mapper.select:

SELECT COUNT(*)
  FROM `tb_config` t
 WHERE t.`is_deleted` = 0


 AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`key` LIKE #{key,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`is_system` = #{system} 


 AND t.`create_time` >= DATE_FORMAT(#{start},'%Y-%m-%d') 


 AND t.`create_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)

SELECT t.`id`, t.`name`, t.`key`, t.`value`, t.`remark`, t.`is_system` AS `system`, t.`create_time` AS createTime FROM `tb_config` t WHERE t.`is_deleted` = 0
AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler}
AND t.`key` LIKE #{key,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler}
AND t.`is_system` = #{system}
AND t.`create_time` >= DATE_FORMAT(#{start},'%Y-%m-%d')
AND t.`create_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)
ORDER BY t.`id` DESC LIMIT #{offset}, #{pageSize}

SELECT t.`id`, t.`name`, t.`key`, t.`value`, t.`remark`, t.`is_system` AS `system` FROM `tb_config` t WHERE t.`is_deleted` = 0
AND t.`id` = #{id}
AND t.`key` = #{key}
