mapper.select:

SELECT COUNT(*)
  FROM `tb_dict_data` t
 WHERE t.`is_deleted` = 0


 AND t.`type_id` = #{typeId} 


 AND t.`type_value` = #{typeValue} 


 AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`status` = #{status}


SELECT t.`id`,
	   t.`type_id` AS typeId,
	   t.`type_value` AS typeValue,
	   t.`name`,
	   t.`value`,
	   t.`remark`,
	   t.`order`,
	   t.`status`,
	   t.`create_time` AS createTime
  FROM `tb_dict_data` t
 WHERE t.`is_deleted` = 0


 AND t.`type_id` = #{typeId} 


 AND t.`type_value` = #{typeValue} 


 AND t.`type_value` IN 
	#{typeValue}


 AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`status` = #{status} 


 ORDER BY t.`type_id`, t.`order`, t.`id` DESC
 LIMIT #{offset}, #{pageSize}

SELECT t.`id`, t.`type_id` AS typeId, t.`type_value` AS typeValue, t.`name`, t.`value`, t.`remark`, t.`order`, t.`status` FROM `tb_dict_data` t WHERE t.`is_deleted` = 0
AND t.`id` = #{id}
AND t.`type_value` = #{typeValue}
AND t.`value` = #{value}
AND t.`status` = #{status}
ORDER BY t.`id` DESC LIMIT 1

mapper.update:

UPDATE `tb_dict_data` t
   SET t.`update_time` = NOW(),
	   t.`modifier` = #{modifier},
	   t.`type_value` = #{typeValue}
 WHERE t.`is_deleted` = 0
   AND t.`type_id` = #{typeId}

UPDATE `tb_dict_data` t SET t.`update_time` = NOW(), t.`modifier` = #{modifier}, t.`is_deleted` = 1 WHERE t.`is_deleted` = 0
AND t.`id` = #{id}
AND t.`type_id` = #{typeId}
