mapper.select:

SELECT COUNT(*)
  FROM `tb_login_log` t
 WHERE t.`is_deleted` = 0


 AND t.`username` LIKE #{username,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`ip` = #{ip} 


 AND t.`status` = #{status} 


 AND t.`login_time` >= DATE_FORMAT(#{start},'%Y-%m-%d') 


 AND t.`login_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)

SELECT t.`id`, t.`username`, t.`ip`, t.`ip_addr` AS ipAddr, t.`login_time` AS loginTime, t.`status`, t.`err_msg` AS errMsg FROM `tb_login_log` t WHERE t.`is_deleted` = 0
AND t.`username` LIKE #{username,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler}
AND t.`ip` = #{ip}
AND t.`status` = #{status}
AND t.`login_time` >= DATE_FORMAT(#{start},'%Y-%m-%d')
AND t.`login_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)
ORDER BY ${sort} ${dir}, t.`id` DESC LIMIT #{offset}, #{pageSize}
