mapper.select:

SELECT COUNT(*)
  FROM `tb_oper_log` t
 WHERE t.`is_deleted` = 0


 AND t.`clazz` LIKE #{clazz,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHanlderCallback} 


 AND t.`operator` LIKE #{operator,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHanlderCallback} 


 AND t.`status` = #{status} 


 AND t.`oper_time` >= DATE_FORMAT(#{start},'%Y-%m-%d') 


 AND t.`oper_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY) 


 AND (t.`corp_id` = #{corpId} OR t.`oper_corp_id` = #{corpId})

SELECT t.`id`, t.`clazz`, t.`request_uri` AS requestUri, t.`request_method` AS requestMethod, t.`request_params` AS requestParams, t.`operator`, t.`ip`, t.`ip_addr` AS ipAddr, t.`oper_time` AS operTime, t.`oper_desc` AS operDesc, t.`status`, t.`err_msg` AS errMsg FROM `tb_oper_log` t WHERE t.`is_deleted` = 0
AND t.`clazz` LIKE #{clazz,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHanlderCallback}
AND t.`operator` LIKE #{operator,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHanlderCallback}
AND t.`status` = #{status}
AND t.`oper_time` >= DATE_FORMAT(#{start},'%Y-%m-%d')
AND t.`oper_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)
AND (t.`corp_id` = #{corpId} OR t.`oper_corp_id` = #{corpId})
ORDER BY ${sort} ${dir}, t.`id` DESC LIMIT #{offset}, #{pageSize}
