mapper.select:

SELECT COUNT(*)
  FROM `tb_role` t
 WHERE t.`is_deleted` = 0


 AND t.`id` = #{id} 


 AND t.`code` LIKE #{code,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler} 


 AND t.`status` = #{status} 


 AND t.`create_time` >= DATE_FORMAT(#{start},'%Y-%m-%d') 


 AND t.`create_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)

SELECT t.`id`, t.`code`, t.`name`, t.`remark`, t.`order`, t.`status`, t.`create_time` AS createTime FROM `tb_role` t WHERE t.`is_deleted` = 0
AND t.`code` LIKE #{code,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler}
AND t.`name` LIKE #{name,typeHandler=com.example.demo.framework.mybatis.type.WildcardTypeHandler}
AND t.`status` = #{status}
AND t.`create_time` >= DATE_FORMAT(#{start},'%Y-%m-%d')
AND t.`create_time` < DATE_ADD(DATE_FORMAT(#{end},'%Y-%m-%d'),interval 1 DAY)
ORDER BY t.`order`, t.`id` DESC LIMIT #{offset}, #{pageSize}

SELECT t.`id`, t.`code`, t.`name`, t.`remark`, t.`order`, t.`status` FROM `tb_role` t WHERE t.`is_deleted` = 0
AND t.`id` = #{id}
AND t.`code` = #{code}

mapper.update:

UPDATE `tb_role` t
   SET t.`update_time` = NOW(),
	   t.`modifier` = #{modifier},
	   t.`status` = #{status}
 WHERE t.`is_deleted` = 0
   AND t.`id` = #{id}
