mapper.update:

UPDATE `tb_chat` t
   SET t.`update_time` = NOW(),
	   t.`modifier` = #{modifier},
	   t.`chat_time` = #{chatTime},
	   t.`chat_detail_id` = #{chatDetailId},
	   t.`unread` = t.`unread` + #{unread}
 WHERE t.`is_deleted` = 0
   AND t.`user_id` = #{userId}
   AND t.`friend_id` = #{friendId}


UPDATE `tb_chat` t
   SET t.`update_time` = NOW(),
	   t.`modifier` = #{modifier},
	   t.`unread` = #{unread}
 WHERE t.`is_deleted` = 0
   AND t.`id` = #{id}
   AND t.`user_id` = #{userId}
