mapper.select:

SELECT COUNT(*)
  FROM `tb_subscribe` t
 WHERE t.`is_deleted` = 0
   AND t.`user_id` = #{userId}
   AND t.`app_id` = #{appId}
   AND t.`scene` = #{scene}
   AND t.`scene_id` = #{sceneId}


SELECT COUNT(*)
  FROM `tb_subscribe` t,
	   `tb_tunnel` p
 WHERE t.`user_id` = p.`user_id`
   AND t.`is_deleted` = 0
   AND p.`is_deleted` = 0
   AND t.`user_id` = #{userId}
   AND t.`scene` = #{scene}
   AND t.`scene_id` = #{sceneId}


SELECT t.`id`,
	   t.`user_id` AS userId,
	   t.`app_id` AS appId,
	   t.`scene` AS scene,
	   t.`scene_id` AS sceneId
  FROM `tb_subscribe` t
 WHERE t.`is_deleted` = 0
   AND t.`user_id` = #{userId}
   AND t.`app_id` = #{appId}
   AND t.`scene` = #{scene}
   AND t.`scene_id` = #{sceneId}
LIMIT 1
